package com.looping;

public class Printnumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Print numbers using for loop
		System.out.println("Print the numbers using for loop:");
		for(int i=10; i<51; i++)
		{
			System.out.println(i);
		}
		
			
		//print numbers suing while loop
		int j=10;
		System.out.println("Print the numbers using while loop:");
		while(j<51)
		{
			System.out.println(j);
			j++;
		}

	}

}
